mapper.select:

SELECT T_Z.*
FROM
    (
    SELECT ROWNUM AS RNUM, T_A.*
    FROM
        (SELECT * FROM TBL_BOARD 
        WHERE 1 = 1



        ORDER BY BNO DESC
        ) T_A
    WHERE ROWNUM <= (#{startNo}/#{perPageNum}+1) * #{perPageNum}
    ) T_Z
 WHERE
    T_Z.RNUM > (#{startNo}/#{perPageNum}) * #{perPageNum}
